import React, { Component } from "react";
import './main.css'
import PropTypes from 'prop-types'

import {
  Container,
  Grid,
  Header,
  Icon,
  Image,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
} from 'semantic-ui-react'

const HomepageHeading = ({ mobile }) => (
  <Container text>
    <Header
      as='h1'
      className= 'title'
      content='Doug Enas'
      inverted
      style={{
        fontSize: mobile ? '1em' : '2em',
        fontWeight: 'normal',
        marginBottom: 0,
        marginTop: mobile ? '1.5em' : '3em',
        textAlign: 'center',
      }}
    />
  </Container>
)


const MainImage = () => (
  <Image
  src={require('./Images/profile.jpg')}
  size='small'
  className='mainPhoto'>

  </Image>
)

const HometpageBio = () => (
  <Container text>
  <Segment className='bio'>
  <p>Innovatov stimulates venture and growth enterprises towards their goal to develop novel, sustainable solutions for significant societal problems. We engage with decision-makers to develop and commercialize breakthrough products and services in health care, biopharmaceutical, technology and other industries. </p>
  <p>Greg Enas, Ph.D is a venture catalyst with extensive experience leading regulatory strategy and evidence-based decision analytics for the development and commercialization of biopharmaceutical and technology products. He has also created or led projects and enterprises devoted to education, arts, culture formation, and human leadership development. He now guides decision makers with actionable insights which maximize enterprise value, mitigate risk, and decrease time to desired outcomes, enhancing solution quality, value, and impact.</p> 
  <p>Dr Enas worked for almost 30 years at Eli Lilly and Company as an organizational and practice leader in the regulatory, statistical and decision sciences. There he helped develop and bring to market many novel molecular entities across multiple therapeutic areas. In the broader civic arena, he has helped develop  educational, human-centric and culture-care solutions to pressing problems. He is a Fellow of the American Statistical Association and obtained a PhD in Biostatistics from Medical College of Virginia/Virginia Commonwealth University. </p>

  </Segment>
  </Container>
)

const Contact = ({ mobile }) => (
  <Container text>
  <Header as='h2' className='title' content='Contact' style={{
        fontSize: mobile ? '1.5em' : '1.7em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1.5em',
        textAlign: 'center',
      }}>

  </Header>
  <p className= "bio">Dr Enas is available for select projects that have significant potential to impact human health, wellbeing  and flourishing. Send your contact and relevant background information to <a href="mailto:sanedad@gmail.com">sanedad@gmail.com</a>.
</p>
  </Container>
)

HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
}

class DesktopContainer extends Component {
  state = {}

  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })

  render() {
    const { children } = this.props
    const { fixed } = this.state

    return (
      <Responsive minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
          <Segment
            inverted
            textAlign='center'
            style={{ minHeight: 700, padding: '1em 0em' }}
            vertical
          >
            <Menu
              fixed={fixed ? 'top' : null}
              inverted={!fixed}
              pointing={!fixed}
              secondary={!fixed}
              size='large'
            >

            </Menu>
            <HomepageHeading />
            <MainImage />


            <HometpageBio />
            <Contact />
          </Segment>
        </Visibility>

        {children}
      </Responsive>
    )
  }
}

DesktopContainer.propTypes = {
  children: PropTypes.node,
}

class MobileContainer extends Component {
  state = {}

  handleSidebarHide = () => this.setState({ sidebarOpened: false })

  handleToggle = () => this.setState({ sidebarOpened: true })

  render() {
    const { children } = this.props
    const { sidebarOpened } = this.state

    return (
      <Responsive as={Sidebar.Pushable} maxWidth={Responsive.onlyMobile.maxWidth}>
        <Sidebar
          as={Menu}
          animation='push'
          inverted
          onHide={this.handleSidebarHide}
          vertical
          visible={sidebarOpened}
        >

        </Sidebar>

        <Sidebar.Pusher dimmed={sidebarOpened}>
          <Segment
            inverted
            textAlign='center'
            style={{ minHeight: 350, padding: '1em 0em' }}
            vertical
          >
            <Container>
              <Menu inverted pointing secondary size='large'>
                <Menu.Item onClick={this.handleToggle}>
                  <Icon name='sidebar' />
                </Menu.Item>
                <Menu.Item position='right'>

                </Menu.Item>
                <HomepageHeading />
                <MainImage />



            <HometpageBio />
            <Contact />
              </Menu>
            </Container>

          </Segment>

          {children}
        </Sidebar.Pusher>
      </Responsive>
    )
  }
}

MobileContainer.propTypes = {
  children: PropTypes.node,
}


const ResponsiveContainer = ({ children }) => (
  <div>
    <DesktopContainer>{children}</DesktopContainer>
    <MobileContainer>{children}</MobileContainer>

  </div>
)

ResponsiveContainer.propTypes = {
  children: PropTypes.node,
}

const HomepageLayout = () => (
  <ResponsiveContainer>
    <Segment style={{ padding: '8em 0em' }} vertical>
      <Grid container stackable verticalAlign='middle'>
        </Grid>

    </Segment>
  </ResponsiveContainer>
)
export default HomepageLayout
