import React, { Component } from "react";
import './main.css'
import PropTypes from 'prop-types'

import {
  Button,
  Container,
  Divider,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
} from 'semantic-ui-react'

const HomepageHeading = ({ mobile }) => (
  <Container text>
    <Header
      as='h1'
      className= 'title'
      content='Innovatov'
      inverted
      style={{
        fontSize: mobile ? '2em' : '4em',
        fontWeight: 'normal',
        marginBottom: 0,
        marginTop: mobile ? '1.5em' : '3em',
        textAlign: 'center',
      }}
    />
    <Header
      as='h2'
      className= "title"
      content='In not of, Not yet now.'
      inverted
      style={{
        fontSize: mobile ? '1.5em' : '1.7em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1.5em',
        textAlign: 'center',
      }}
    />
  </Container>
)

const HomepageBioOne = () => (
  <Container text>
  <Segment className='bio'>
  <p>Greg Enas, Ph.D. provides strategic regulatory advice and fosters evidence-based decision making for the biopharmaceutical industry. He has extensive corporate organizational and thought leadership at the intersection of US regulatory affairs,  global statistical science, and decision science. This unique background enables him to guide corporate teams toward actionable insights with regulatory agencies  that maximize value, mitigate risk, decrease time, and enhance the quality of drug development programs. </p>

  </Segment>
  </Container>
)
const MainImage = () => (
  <Image
  src={require('./Images/gregenas.jpg')}
  size='small'
  className='mainPhoto'>

  </Image>
)

const HometpageBioTwo = () => (
  <Container text>
  <Segment className='bio'>
  <p>Dr. Enas worked for almost 30 years at Eli Lilly and Company, completing his tenure in 2011, prior to assuming his current role as catalyst and counselor within the biopharmaceutical industry. He is a Fellow of the American Statistical Association, and obtained the PhD in Biostatistics from Medical College of Virginia/Virginia Commonwealth University in 1982 following completion of the BS in Mathematical Sciences from Biola College in 1978. </p>
  </Segment>
  </Container>
)

const Contact = () => (
  <Container text>
  <Header as='h2' className='title' content='Contact'>

  </Header>
  </Container>
)

HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
}

/* Heads up!
 * Neither Semantic UI nor Semantic UI React offer a responsive navbar, however, it can be implemented easily.
 * It can be more complicated, but you can create really flexible markup.
 */
class DesktopContainer extends Component {
  state = {}

  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })

  render() {
    const { children } = this.props
    const { fixed } = this.state

    return (
      <Responsive minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
          <Segment
            inverted
            textAlign='center'
            style={{ minHeight: 700, padding: '1em 0em' }}
            vertical
          >
            <Menu
              fixed={fixed ? 'top' : null}
              inverted={!fixed}
              pointing={!fixed}
              secondary={!fixed}
              size='large'
            >

            </Menu>
            <HomepageHeading />
            <HomepageBioOne />
            <MainImage />
            <HometpageBioTwo />
            <Contact />
          </Segment>
        </Visibility>

        {children}
      </Responsive>
    )
  }
}

DesktopContainer.propTypes = {
  children: PropTypes.node,
}



const ResponsiveContainer = ({ children }) => (
  <div>
    <DesktopContainer>{children}</DesktopContainer>

  </div>
)

ResponsiveContainer.propTypes = {
  children: PropTypes.node,
}

const HomepageLayout = () => (
  <ResponsiveContainer>
    <Segment style={{ padding: '8em 0em' }} vertical>
      <Grid container stackable verticalAlign='middle'>
        </Grid>

    </Segment>
  </ResponsiveContainer>
)
export default HomepageLayout
