import React, { Component } from "react";
import './main.css'
import PropTypes from 'prop-types'

import {
  Button,
  Container,
  Divider,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
} from 'semantic-ui-react'


const HomepageHeading = ({ mobile }) => (
  <Container text>
    <Header
      as='h1'
      className= 'title'
      content='Innovatov'
      inverted
      style={{
        fontSize: mobile ? '2em' : '4em',
        fontWeight: 'normal',
        marginBottom: 0,
        marginTop: mobile ? '1.5em' : '3em',
        textAlign: 'center',
      }}
    />
    <Header
      as='h2'
      className= "title"
      content='In not of, Not yet now.'
      inverted
      style={{
        fontSize: mobile ? '1.5em' : '1.7em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1.5em',
        textAlign: 'center',
      }}
    />
  </Container>
)

const HomepageBioOne = () => (
  <Container text>
  <Segment className='bio'>
  <p>Greg Enas, Ph.D. provides strategic drug development advice to enable evidence-based decision making for the biopharmaceutical industry. He has extensive corporate organizational and thought leadership across the development lifecycle including regulatory, statistical and decision sciences. This unique background enables him to guide corporate teams toward actionable insights at critical junctures that maximize value, mitigate risk, decrease time, and enhance the quality of drug development programs.  </p>

  </Segment>
  </Container>
)
const MainImage = () => (
  <Image
  src={require('./Images/gregenas.PNG')}
  size='small'
  className='mainPhoto'>

  </Image>
)

const MainLogo = () => (
  <Image
    src={require("./Images/innovativelogo.jpg")}
    size='small'
    >

  </Image> 
  )

const HometpageBioTwo = () => (
  <Container text>
  <Segment className='bio'>
  <p>Dr Enas worked for almost 30 years at Eli Lilly and Company and impacted the development or market authorization of dozens of novel molecular entities across multiple therapeutic areas.  </p><br></br>
  <p>He is a Fellow of the American Statistical Association, and obtained the PhD in Biostatistics from Medical College of Virginia/Virginia Commonwealth University in 1982 following completion of the BS in Mathematical Sciences from Biola College in 1978. 
</p>
  </Segment>
  </Container>
)

const Contact = ({ mobile }) => (
  <Container text>
  <Header as='h2' className='title' content='Contact' style={{
        fontSize: mobile ? '1.5em' : '1.7em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1.5em',
        textAlign: 'center',
      }}>

  </Header>
  <p className= "bio">Email: sanedad@gmail.com or call 317-328-9213 to learn more</p>
  </Container>
)

HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
}

/* Heads up!
 * Neither Semantic UI nor Semantic UI React offer a responsive navbar, however, it can be implemented easily.
 * It can be more complicated, but you can create really flexible markup.
 */
class DesktopContainer extends Component {
  state = {}

  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })

  render() {
    const { children } = this.props
    const { fixed } = this.state

    return (
      <Responsive minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
          <Segment
            inverted
            textAlign='center'
            style={{ minHeight: 700, padding: '1em 0em' }}
            vertical
          >
            <Menu
              fixed={fixed ? 'top' : null}
              inverted={!fixed}
              pointing={!fixed}
              secondary={!fixed}
              size='large'
            >

            </Menu>
            <MainLogo />
            <HomepageBioOne />
            <MainImage />
            <HometpageBioTwo />
            <Contact />
          </Segment>
        </Visibility>

        {children}
      </Responsive>
    )
  }
}

DesktopContainer.propTypes = {
  children: PropTypes.node,
}

class MobileContainer extends Component {
  state = {}

  handleSidebarHide = () => this.setState({ sidebarOpened: false })

  handleToggle = () => this.setState({ sidebarOpened: true })

  render() {
    const { children } = this.props
    const { sidebarOpened } = this.state

    return (
      <Responsive as={Sidebar.Pushable} maxWidth={Responsive.onlyMobile.maxWidth}>
        <Sidebar
          as={Menu}
          animation='push'
          inverted
          onHide={this.handleSidebarHide}
          vertical
          visible={sidebarOpened}
        >

        </Sidebar>

        <Sidebar.Pusher dimmed={sidebarOpened}>
          <Segment
            inverted
            textAlign='center'
            style={{ minHeight: 350, padding: '1em 0em' }}
            vertical
          >
            <Container>
              <Menu inverted pointing secondary size='large'>
                <Menu.Item onClick={this.handleToggle}>
                  <Icon name='sidebar' />
                </Menu.Item>
                <Menu.Item position='right'>

                </Menu.Item>
                <MainLogo/>>
            <HomepageBioOne />
            <MainImage />
            <HometpageBioTwo />
            <Contact />
              </Menu>
            </Container>

          </Segment>

          {children}
        </Sidebar.Pusher>
      </Responsive>
    )
  }
}

MobileContainer.propTypes = {
  children: PropTypes.node,
}


const ResponsiveContainer = ({ children }) => (
  <div>
    <DesktopContainer>{children}</DesktopContainer>
    <MobileContainer>{children}</MobileContainer>

  </div>
)

ResponsiveContainer.propTypes = {
  children: PropTypes.node,
}

const HomepageLayout = () => (
  <ResponsiveContainer>
    <Segment style={{ padding: '8em 0em' }} vertical>
      <Grid container stackable verticalAlign='middle'>
        </Grid>

    </Segment>
  </ResponsiveContainer>
)
export default HomepageLayout
